

# LibTSforge.dll dosyası	 --> Windows_Addict (Massgrave)
# activate.ps1 betik dosyası --> Dark Vador

# Tüm övgüler bu üstatlara :)


# activate.ps1 betik dosyası Abdullah ERTÜRK (github.com/abdullah-erturk) tarafından otomatik kullanım için değiştirildi.


param (
    [switch]$w,
    [switch]$o
)

if (-not $PSScriptRoot) {
    $PSScriptRoot = Split-Path -Parent $MyInvocation.MyCommand.Definition
}

# LibTSforge.dll, Sıkıştırılmış ve Base64 olarak kodlanmış
$base64EncodedDll = @"

"@

$dllPath = Join-Path $PSScriptRoot 'LibTSforge.dll'
if (Test-Path $dllPath) {
    [Reflection.Assembly]::LoadFrom($dllPath) | Out-Null
} else {
    $compressedDllBytes = [Convert]::FromBase64String($base64EncodedDll)
    $compressedStream = [System.IO.MemoryStream]::new($compressedDllBytes)
    $gzipStream = New-Object System.IO.Compression.GZipStream($compressedStream, [System.IO.Compression.CompressionMode]::Decompress)
    $decompressedStream = New-Object System.IO.MemoryStream
    $gzipStream.CopyTo($decompressedStream)
    $gzipStream.Close()
    $decompressedDllBytes = $decompressedStream.ToArray()
    [System.Reflection.Assembly]::Load($decompressedDllBytes)
}

function GetRandomKey([string]$ProductID) {
    try {
        $guid = [Guid]::Parse($ProductID)
        $pkc = [LibTSforge.SPP.PKeyConfig]::new()
        try { $pkc.LoadConfig($guid) | Out-Null } catch { $pkc.LoadAllConfigs([LibTSforge.SPP.SLApi]::GetAppId($guid)) | Out-Null }
        $config = [LibTSforge.SPP.ProductConfig]::new()
        $refConfig = [ref]$config
        $pkc.Products.TryGetValue($guid, $refConfig) | Out-Null
        return $config.GetRandomKey().ToString()
    } catch {
		Write-Host
        Write-Host "Ürün Kimliği için anahtar alınamadı: $ProductID" -ForegroundColor Red
		Write-Host
		return $null
    }
}

# Orijinal Office Lisansı uyarı afişini önlemek için rastgele KMS IP adresi oluşturma işlevi
function Set-RandomKMSAddress {
    param(
        [string]$productDescription
    )
    
    $RegPath = "HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion\SoftwareProtectionPlatform\0ff1ce15-a989-479d-af46-f275c6370663"
    if (Test-Path -Path $RegPath) {
        try {
            Remove-Item -Path $RegPath -Recurse -Force -ErrorAction Stop
        } catch {
            Write-Host 
        }
    }

    # Set KMS address only for products activated with KMS
    if ($productDescription -match 'KMS|VOLUME_KMS') {
        $rand = New-Object System.Random
        $rand_A = [int](192 + $rand.Next(0, 63))     # 193-255
        $rand_B = [int](169 + $rand.Next(0, 87))     # 169-255
        $rand_C = [int](1 + $rand.Next(0, 255))      # 1-255
        $rand_D = [int](1 + $rand.Next(0, 255))      # 1-255

        $ip = "$rand_A.$rand_B.$rand_C.$rand_D"

        try {
            Set-ItemProperty -Path 'HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion\SoftwareProtectionPlatform' -Name 'KeyManagementServiceName' -Value $ip -Force
            Set-ItemProperty -Path 'HKLM:\SOFTWARE\WOW6432Node\Microsoft\Windows NT\CurrentVersion\SoftwareProtectionPlatform' -Name 'KeyManagementServiceName' -Value $ip -Force
            Write-Host
			Write-Host "Rastgele KMS IP adresi atandı: $ip" -ForegroundColor Cyan
        } catch {
            Write-Host
            Write-Warning "Rastgele KMS IP adresi atanamadı: $_"
            Write-Host
        }
    } else {
		Write-Host
        Write-Host "Ürün KMS tabanlı değil, rastgele KMS IP ayarı atlanıyor." -ForegroundColor Yellow
		Write-Host
    }
}

function Activate-License([string]$desc, [string]$ver, [string]$prod, [string]$id) {
    try {
        if ($desc -match 'KMS') {
            [LibTSforge.Activators.KMS4k]::Activate($ver, $prod, $id)
        } elseif ($desc -match 'VIRTUAL_MACHINE_ACTIVATION') {
            [LibTSforge.Activators.AVMA4K]::Activate($ver, $prod, $id)
        } elseif ($desc -match 'RETAIL|MAK|OEM|VOLUME') {
            [LibTSforge.Activators.ZeroCID]::Activate($ver, $prod, $id)
        } else {
		Write-Host
		Write-Host "Bilinmeyen lisans türü: $desc" -ForegroundColor Red
		Write-Host
        }
    } catch {
		Write-Host
		Write-Host "$id için etkinleştirme başarısız oldu : $_" -ForegroundColor Red
		Write-Host
	}
}

function Main {
    $ver = [LibTSforge.Utils]::DetectVersion()
    $prod = [LibTSforge.Utils]::DetectCurrentKey()
    $svc  = Get-WmiObject SoftwareLicensingService
    $iwmi = Get-WmiObject SoftwareLicensingProduct

    $allOffice       = $iwmi | Where-Object { $_.Name -match 'Office' }
    $installedOffice = $allOffice | Where-Object { $_.LicenseStatus -eq 1 }
    $retailOffice    = $allOffice | Where-Object { $_.Description -match 'RETAIL|MAK|OEM|VOLUME' }
    $installedWindows = $iwmi | Where-Object { $_.Name -match 'Windows' -and $_.LicenseStatus -eq 1 }

    $selectionList = @()

    if ($w) {
        $selectionList += $installedWindows
    }

	if ($o) {
		$officeList = @($allOffice) |
			Where-Object {
				$_.Description -match 'RETAIL|MAK|OEM' `
				-and $_.Description -notmatch 'Grace|Free'
			}

		$groups = @{}
		foreach ($item in $officeList) {
			if    ($item.Name -match 'Visio')       { $key = 'Visio' }
			elseif($item.Name -match 'Project')     { $key = 'Project' }
			elseif($item.Name -match 'Standard')    { $key = 'Standard' }
			else                                     { $key = 'ProPlus' }

			if (-not $groups.ContainsKey($key)) {
				$groups[$key] = $item
			}
		}

		$selectionList += $groups.Values
	}

	if (-not $w -and -not $o) {
		$selectionList += $installedWindows

		$combinedOffice = @($installedOffice) + @($retailOffice)

		$officeGridList = $combinedOffice |
			Where-Object {
				$_.Description -match 'RETAIL|MAK|OEM|VOLUME' `
				-and $_.Description -notmatch 'Grace|Free'
			}
		$selectionList += $officeGridList
	}

	$selectionList = $selectionList | Sort-Object Name -Unique

    if ($selectionList.Count -eq 0) {
        Write-Host "Aktivasyon için ürün bulunamadı." -ForegroundColor Red
        return
    }

    # Check if it is licensed
    $fullyLicensed = $true
    foreach ($item in $selectionList) {
        if ($item.Description -notmatch 'RETAIL|MAK|OEM|KMS|VOLUME') {
            $fullyLicensed = $false
            break
        }
    }

	if ($fullyLicensed) {
		Write-Host
		Write-Host "Tüm ürünler lisanslı görünüyor." -ForegroundColor Cyan
		Write-Host
		if ($w -or $o) {
			Write-Host
			Write-Host "Lisanslama süreci sonlandırıldı." -ForegroundColor Yellow
			Write-Host
			return
		}
		else {
			Write-Host
			Write-Host "Lisanslama süreci yine de devam etmeli mi? (E/H): " -NoNewline
			$key = $Host.UI.RawUI.ReadKey("IncludeKeyDown").Character.ToString().ToUpper()
			Write-Host
			if ($key -ne 'E') {
				Write-Host
				Write-Host "Lisanslama süreci sonlandırıldı." -ForegroundColor Yellow
				Write-Host
				return
			}
		}
	}

    if ($w -or $o) {
        $selected = $selectionList
    } else {
        $selected = $selectionList | Select ID, Description, Name |
            Out-GridView -Title 'Lisanslanacak ürünleri seçin ve OK tuşuna basın. Birden fazla ürünü seçmek için CTRL tuşunu kullanın.' -OutputMode Multiple
    }

    if ($selected -and @($selected).Count -ge 1) {
		
        if (-not $w -and -not $o) {
            $kmsProduct = $selected | Where-Object { $_.Description -match 'KMS|VOLUME_KMS' } | Select-Object -First 1
            if ($kmsProduct) {
                Set-RandomKMSAddress -productDescription $kmsProduct.Description
            }
        }

        foreach ($item in $selected) {
            $tsactid = $item.ID
            $key     = GetRandomKey -ProductID $tsactid
			Write-Host
			Write-Host "--- Aktivasyon Başlatılıyor ---" -ForegroundColor Green
			Write-Host
            Write-Host "Kimlik		: $tsactid" -ForegroundColor DarkGreen
            Write-Host "İsim		: $($item.Name)" -ForegroundColor White
            Write-Host "Açıklama	: $($item.Description)" -ForegroundColor DarkGreen
            
			if ($item.Description -match 'VIRTUAL_MACHINE_ACTIVATION') {
                Write-Host "GEREKLİLİKLER: Windows Server Datacenter'ı HOST olarak + çalışması için hyper-V veya QEMU olarak kullanın. Çıktı başarıyı gösterebilir, ancak gerçek durum slmgr.vbs -dlv aracılığıyla gösterilir." -ForegroundColor Yellow
            }
            Write-Host

            if ($key) {
                $svc.InstallProductKey($key) | Out-Null
                Write-Host "Ürün anahtarı: $key"
            } else {
                Write-Host "$($item.Name) için anahtar üretilemedi" -ForegroundColor White
                continue
            }
            Activate-License -desc $item.Description -ver $ver -prod $prod -id $tsactid
            Write-Host "--------------------------------------"
        }
    }
}


# Script start
cls
Write-Host
Write-Host "Lisans ve ürün bilgileri toplanıyor, lütfen sabırla bekleyin..." -ForegroundColor Green
Main

# Exit behavior
if (-not $w -and -not $o) {
    Write-Host
    Write-Host "Çıkış için Enter tuşuna basın" -ForegroundColor Red
    Read-Host | Out-Null
} else {
	Write-Host
    for ($i = 5; $i -ge 1; $i--) {
        Write-Host -NoNewline ("`r{0} saniye içinde otomatik olarak kapanıyor..." -f $i) -ForegroundColor Yellow
        Start-Sleep -Seconds 1
    }
}